// container adding
import 'package:flutter/material.dart';
import 'package:project/bp.dart';
import 'package:project/fall_det.dart';
import 'package:project/heartrate.dart';

import 'third_page.dart';

class SecondPage extends StatefulWidget {
  const SecondPage({Key? key}) : super(key: key);

  @override
  _SecondPageState createState() => _SecondPageState();
}

class _SecondPageState extends State<SecondPage> {
  void navigateToNextPage(String image) {
    switch (image) {
      case 'temperature':
        Navigator.push(context, MaterialPageRoute(builder: (context) => const ThirdPage()));
        break;
      case 'blood_pressure':
        Navigator.push(context, MaterialPageRoute(builder: (context) => const BloodPressure()));
        break;
      case 'heart_rate':
        Navigator.push(context, MaterialPageRoute(builder: (context) => const HeartRate()));
        break;
      case 'fall_detection':
        Navigator.push(context, MaterialPageRoute(builder: (context) => const FallDetection()));
        break;
      default:
        break;
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      //appBar: AppBar(
      //title: const Text('Medical App'),
      //),
      body: Container(color: Colors.blue,
      child: GridView.count(
        crossAxisCount: 2,
        children: [
          buildCircularContainer('Temperature', 'data:image/png;base64,',
           () => navigateToNextPage('temperature')),
          buildCircularContainer('Blood Pressure', 'https://static.vecteezy.com/system/resources/previews/025/782/718/original/blood-pressure-icon-free-vector.jpg', () => navigateToNextPage('blood_pressure')),
          buildCircularContainer('Heart Rate', 'data:image/jpeg;base64,', () => navigateToNextPage('heart_rate')),
          buildCircularContainer('Fall Detection', 'data:image/jpeg;base64,', () => navigateToNextPage('fall_detection')),
          buildCircularContainer('Assistance', 'data:image/png;base64,', () => navigateToNextPage('assistance')),
          buildCircularContainer('Emergency Care', 'data:image/png;base64,', () => navigateToNextPage('emergency_care')),
        ],
      ),
      )
    );
  }

  Widget buildCircularContainer(String text, String imageUrl, VoidCallback onTap) {
    return GestureDetector(
      onTap: onTap,
      child: Container(
       width: 120,
       height: 120,
        decoration: BoxDecoration(
          shape: BoxShape.circle,
          color: Colors.white,
          //borderRadius:  BorderRadius.circular(70.0),
          
          boxShadow: [
            BoxShadow(
              color: Colors.black.withOpacity(0.5),
              spreadRadius: 5,
              blurRadius: 7,
              offset: const Offset(0, 3),
            ),
          ],
        ),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            CircleAvatar(
              radius: 50,
              backgroundImage: NetworkImage(imageUrl),
              
            ),
            const SizedBox(height: 5),
            Text(
              text,
              style: const TextStyle(fontSize: 16, fontWeight: FontWeight.bold),
            ),
          ],
        ),
      ),
    );
  }
}

